mapper.select:
Select * 
 From review 
 Where gServNo=${gservNo} 
   and rGroup=${rgroup} 
   and rStep != 0
 Order by rStep

mapper.delete:
Delete review 
 Where rGroup  = ${rgroup} 
   and gServNo = ${gservNo}

Delete review
 Where rGroup  = ${rgroup}
   and gServNo = ${gservNo}
   and rStep   = ${rstep}
